// n8n Code node (JavaScript)

const html = $json.data || '';

// Регулярний вираз для повних лінок на вакансії LinkedIn (з параметрами)
const regex = /https:\/\/de\.linkedin\.com\/jobs\/view\/[a-z0-9\-]+/gi;

// Знаходимо всі збіги
const matches = html.match(regex) || [];

// Видаляємо дублікати
const uniqueLinks = [...new Set(matches)];

// Повертаємо результат у форматі n8n
return uniqueLinks.map(url => ({ json: { link: url } }));

const allItems = $items();
const allResults = [];

for (const item of allItems) {
  // Знаходимо текст із JSON
  let raw = null;

  if (Array.isArray(item.json.output)) {
    const found = item.json.output.find(o => o?.content?.[0]?.text);
    raw = found ? found.content[0].text : null;
  } else if (item.json.output?.content?.[0]?.text) {
    raw = item.json.output.content[0].text;
  }

  if (!raw) continue;

  // Переконуємось, що це рядок
  if (typeof raw !== "string") {
    raw = JSON.stringify(raw);
  }

  // Прибираємо \n, \t і зайві пробіли
  const cleaned = raw.replace(/\\n/g, '').replace(/\\t/g, '').trim();

  let companies = [];
  try {
    companies = JSON.parse(cleaned);
  } catch (e) {
    console.log("❌ JSON parse error:", e.message);
    continue;
  }

  // Розгортання полів Hiring Manager, якщо їх кілька
  for (const company of companies) {
    const names = Array.isArray(company["Hiring Manager Name"])
      ? company["Hiring Manager Name"]
      : [company["Hiring Manager Name"]];

    names.forEach((name, i) => {
      allResults.push({
        json: {
          ...company,
          "Hiring Manager Name": name,
          "Hiring Manager Position": Array.isArray(company["Hiring Manager Position"])
            ? company["Hiring Manager Position"][i]
            : company["Hiring Manager Position"],
          "Hiring Manager Linkedin URL": Array.isArray(company["Hiring Manager Linkedin URL"])
            ? company["Hiring Manager Linkedin URL"][i]
            : company["Hiring Manager Linkedin URL"],
        },
      });
    });
  }
}

return allResults;

// Отримуємо HTML і базову частину лінки
const html = $('HTTP Request - get vacancies').item.json.data || '';
const baseLink = $('HTTP - scrap each vacancy').item.json.Vacancy || '';

// Очистимо обидва значення
const htmlClean = html.replace(/&amp;/g, '&');
const base = baseLink.trim();

// Якщо нема базової лінки — одразу вихід
if (!base) {
  return [{ json: { matchedLink: null, reason: 'No base Vacancy link found' } }];
}

// Шукаємо всі посилання на вакансії
const regex = /https?:\/\/[a-z]{0,3}\.?linkedin\.com\/jobs\/view\/[^\s"'<>]+/gi;
const matches = htmlClean.match(regex) || [];

// Знаходимо перший URL, який починається з base
const matched = matches.find(url => url.startsWith(base)) || null;

// Повертаємо тільки одну знайдену лінку
return [
  {
    json: {
      baseLink: base,
      matchedLink: matched
    }
  }
];

